package com.algo.practice.module.string;

public class FindDifference {
	/* https://leetcode.com/problems/find-the-difference/submissions/ */
	public static void main(String[] args) {
		System.out.println(findTheDifference2("abcd", "abcde"));
		System.out.println(findTheDifference2(
				"",
				""));
	}

	public static char findTheDifference(String s, String t) {
		if (s.isEmpty())
			return t.toCharArray()[0];

		for (int i = 0; i < t.length(); i++) {
			if (i < s.length()) {
				if (s.charAt(i) != t.charAt(i))
					return t.charAt(i);
			} else
				return t.charAt(i);

		}

		throw new IllegalArgumentException();
	}

	public static char findTheDifference2(String s, String t) {
		int j = 0;
		int k = 0;
		for (int i = 0; i < s.length(); i++) {
			k += s.charAt(i);
			j += t.charAt(i);
		}
		j += t.charAt(t.length() - 1);
		return (char) (j - k);
	}
}
